<?phprequire_once("config.php");require_once("mysql.php");$no = null;$password = null;if (isset($_POST["user_no"])){    $no = $_POST["user_no"];}if (isset($_POST["user_password"])){    $password = $_POST["user_password"];}$ret = false;if ($no != null || $password != null){    global $db;    $dboperator = new MysqlOperator($db["name"],$db["user"],$db["password"],$db["database"],'pconn', 'UTF8');    $sql = "select count(*) as count from info where no='$no' and password='$password'";    $dboperator->query($sql);    $result = $dboperator->fetch_array();    if ($result != null && $result[0] == 1)    {        $ret = true;    }}if ($ret == false){    header("Location: login_error.html");}else    // ¼ɹ{    session_start();    $_SESSION['no'] = $no;    header("Location: list.php");}?>